CREATE TABLE IF NOT EXISTS `blogdb`.`posts` (`postid` INT NOT NULL AUTO_INCREMENT, `subject` VARCHAR(45) NULL DEFAULT NULL, `content` LONGTEXT NULL DEFAULT NULL, `username` VARCHAR(45) CHARACTER SET 'armscii8' NULL DEFAULT NULL, PRIMARY KEY (`postid`), UNIQUE INDEX `postid_UNIQUE` (`postid` ASC) VISIBLE);
CREATE TABLE IF NOT EXISTS `blogdb`.`users` (`userid` INT NOT NULL AUTO_INCREMENT, `username` VARCHAR(45) NULL DEFAULT NULL, `name` VARCHAR(45) NULL DEFAULT NULL, `birthday` DATE NULL DEFAULT NULL, `email` VARCHAR(45) NULL DEFAULT NULL, `secretinfo` VARCHAR(45) NULL DEFAULT NULL, `password` VARCHAR(45) NULL DEFAULT NULL, PRIMARY KEY (`userid`), UNIQUE INDEX `userid_UNIQUE` (`userid` ASC) VISIBLE);